def BackgroundSwitch(level,sad, sick, angry,happy)-> str:
    if pageNumber == 1:
        if level == 10:
            return "gameover"
        elif level == range(7,9):
            if sick > 0:
                return "starsick"
            elif angry >0:
                return "starangry"
            elif sad > 0:
                return "starsad"
            else :
                if happy > 8:
                    return "superhappystar"
                elif happy == range(7,8):
                    return "happystar"
                elif happy == range(5,6):
                    return "averagestar"
                else:
                    return "sadstar"
        elif level ==range(3,6):
            if sick > 0:
                return "nebulasick"
            elif angry >0:
                return "nebulaangry"
            elif sad > 0:
                return "nebulasad"
            else :
                if happy > 8:
                    return "superhappynebula"
                elif happy == range(7,8):
                    return "happynebula"
                elif happy == range(5,6):
                    return "averagenebula"
                else:
                    return "sadnebula"
        else :
            if sick > 0:
                return "gassick"
            elif angry >0:
                return "gasangry"
            elif sad > 0:
                return "gassad"
            else :
                if happy > 8:
                    return "superhappygas"
                elif happy == range(7,8):
                    return "happygas"
                elif happy == range(5,6):
                    return "averagegas"
                else:
                    return "sadgas"
    elif pageNumber == 2:
        return random.choice(gameLibrary)
    elif pageNumber == 3:
        if selector == 1:
            return "funfood"
        elif selector == 2:
            return "meteor"
        elif selector == 3:
            return "shootingstar"
        else:
            return "Food"
    elif pageNumber == 4:
        if selector == 1:
            return "animal"
        elif selector == 2:
            return "spaceship"
        elif selector == 3:
            return "satelite"
        else:
            return "toy"
    elif pageNumber == 5:
        if selector == 1:
            return "bath"
        elif selector == 2:
            return "brush"
        elif selector == 3:
            return "medicine"
        else:
            return "hyg"
    else:
        return "startscreen"

pagetype =(BackgroundSwitch(starpet.level,
                            starpet.sad,
                            starpet.sick,
                            starpet.angry,
                            starpet.happy))         


